func findValidatorIndexBruteforce(
    validators: openArray[Validator], bsv: BucketSortedValidators,
    h2: ValidatorPubKey): Opt[ValidatorIndex] =
  for validatorIndex in bsv.extraItems:
    if validators[validatorIndex.distinctBase].pubkey == h2:
      return Opt.some validatorIndex.ValidatorIndex
  for validatorIndex in bsv.bucketSorted:
    if validators[validatorIndex].pubkey == h2:
      return Opt.some validatorIndex.ValidatorIndex
  Opt.none ValidatorIndex

suite "ValidatorPubKey bucket sort":
  setup:
    var hashedPubkeyItems = mapIt(pubkeys, HashedValidatorPubKeyItem(
      key: it.get, root: hash_tree_root(it.get)))
    let
      hashedPubkeys = mapIt(hashedPubkeyItems, HashedValidatorPubKey(
        value: unsafeAddr it))
      validators = mapIt(hashedPubkeys, Validator(pubkeyData: it))

  test "one-shot construction":
    let bsv = sortValidatorBuckets(
      validators.toOpenArray(0, 7*validators.len div 8))
    for vidx in 0 ..< validators.len:
      check findValidatorIndex(validators, bsv[], validators[vidx].pubkey) ==
        findValidatorIndexBruteforce(validators, bsv[], validators[vidx].pubkey)

  test "incremental construction":
    let bsv = sortValidatorBuckets([])
    template rv: untyped = validators.toOpenArray(0, 7*validators.len div 8)
    for vidx in 0 ..< len(rv):
      bsv[].add vidx.ValidatorIndex
    for vidx, validator in validators:
      let foundIdx = findValidatorIndex(rv, bsv[], validator.pubkey)
      check:
        foundIdx == findValidatorIndexBruteforce(rv, bsv[], validator.pubkey)
        foundIdx.isOk == (vidx < rv.len)